import org.omg.CORBA.OBJ_ADAPTER;

import java.util.HashMap;
import java.util.Map;
import java.util.Set;

/**
 * @PackageName:PACKAGE_NAME
 * @ClassName:SolutionWork
 * @Description: leetcode 451 https://leetcode-cn.com/problems/sort-characters-by-frequency/
 * @Autor:CourageHe
 * @Date: 2020/3/25 14:37
 */
public class SolutionWork5 {


    //超出时间限制
    public String frequencySort(String s) {
        Map<Character, Integer> map = new HashMap<>();
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if (map.containsKey(c))
                map.put(c, map.get(c) + 1);
            else
                map.put(c, 1);
        }
        StringBuilder builder = new StringBuilder();
        for (int i = 0; i < s.length(); i++) {
            int max = 0;
            char temp = ' ';
            for (char c:map.keySet()) {
                if(!map.containsKey(c)) continue;
                int num = map.get(c);
                if (max < num) {
                    max = num;
                    temp = c;
                }
            }
            //当 c 对应的 数量为0 时则移除
            map.put(temp, 0);
            map.remove((Object) temp);
            for (int j = 0; j < max; j++)
                builder.append(temp);
        }
        return builder.toString();
    }


    public static void main(String[]args){
        String s = "";

        long startTime = System.currentTimeMillis();

        SolutionWork5 ss = new SolutionWork5();
        String res = ss.frequencySort(s);

        long endTime = System.currentTimeMillis();
        System.out.println("result："+res);
        System.out.println("Two Sum run completely");
        System.out.println("Time cost:"+ (endTime - startTime)+"ms");
    }
}
